};

const postUpload = (req, res) => {
  res.render("upload", { pageTitle: "home", message: "hello!!!!!" });
};

const see = (req, res) => {
  console.log(req.params);
};

app.set("views", process.cwd() + "/src/views");
app.set("view engine", "pug");
app.use(logger);

app.get("/", Home);
